import React from "react"
import Navbar from './Navbar';
import { useState } from "react";
import { Squash as Hamburger } from "hamburger-react";
import { Link } from 'react-router-dom';

const Header = () => {
  const [isOpen, setOpen] = useState(false);
  const closeMenu =() => setOpen(false);
  var prevScrollpos = window.pageYOffset;
window.onscroll = function() {
    var currentScrollPos = window.pageYOffset;
    if (prevScrollpos > currentScrollPos) {
        document.getElementsByClassName("header").style.top = "0";
    } 
    else {
        document.getElementBy("header").style.top = "-50px";
    }
    prevScrollpos = currentScrollPos;
}
  return (
    <center className={isOpen ? 'header active' : 'header'}>
      <div className={isOpen ? 'nav-area active' : 'nav-area'}>
        <Link to="/" className="logo">
          Digichola
        </Link>
        <div className={isOpen ? 'nav-icons active' : 'nav-icons'}>
            <Hamburger toggled={isOpen} size={20} toggle={setOpen} />
        </div>
        <Navbar isOpen={isOpen} closeMenu={closeMenu} />
      </div>
    </center>
  );
};

export default Header;
